    @autoreleasepool {
        long Fibo [30];
        Fibo [0] = 1;
        Fibo [1] = 1;

        for (int i = 2; i < sizeof(Fibo)/sizeof(Fibo[0]); i++) {
            Fibo [i] = Fibo [i-1] + Fibo [i-2];
        }

        for (int i = 0; i < sizeof(Fibo)/sizeof(Fibo[0]); i++) {
            NSLog(@"The result of Fibonacci[%i] is %li\n",i,Fibo[i]);
        }
    }
    return 0;
}
